// --- Callback pour afficher les labels Y avec 1 décimale ---
static void y_axis_draw_event_cb(lv_event_t * e)
{
    lv_obj_draw_part_dsc_t * dsc = lv_event_get_param(e);

    if (dsc->part == LV_PART_TICKS && dsc->text && dsc->id == LV_CHART_AXIS_PRIMARY_Y) {
        int raw = atoi(dsc->text);
        char buf[16];
        snprintf(buf, sizeof(buf), "%.1f", raw / 10.0f);
        lv_snprintf(dsc->text, 16, "%s", buf);
    }
}

// --- Callback pour dessiner le remplissage sous la courbe ---
static void draw_area_fill_cb(lv_event_t * e)
{
    lv_obj_t * obj = lv_event_get_target(e);
    lv_obj_draw_part_dsc_t * dsc = lv_event_get_draw_part_dsc(e);
    
    // On dessine uniquement pour les lignes de la série
    if (dsc->part == LV_PART_ITEMS) {
        if (dsc->p1 && dsc->p2) {
            // Récupérer les coordonnées du graphe
            lv_area_t series_area;
            lv_obj_get_content_coords(obj, &series_area);
            
            // Créer un polygone pour remplir sous la ligne
            lv_point_t points[4];
            points[0].x = dsc->p1->x;
            points[0].y = dsc->p1->y;
            points[1].x = dsc->p2->x;
            points[1].y = dsc->p2->y;
            points[2].x = dsc->p2->x;
            points[2].y = series_area.y2; // Bas du graphe
            points[3].x = dsc->p1->x;
            points[3].y = series_area.y2; // Bas du graphe
            
            // Style de remplissage
            lv_draw_rect_dsc_t rect_dsc;
            lv_draw_rect_dsc_init(&rect_dsc);
            rect_dsc.bg_color = lv_color_hex(0xd11111); // Couleur rouge
            rect_dsc.bg_opa = LV_OPA_20; // Ajustez l'opacité (20-60)
            rect_dsc.border_width = 0; // Pas de bordure
            
            // Dessiner le rectangle entre les deux points
            lv_area_t fill_area;
            fill_area.x1 = LV_MIN(points[0].x, points[1].x);
            fill_area.x2 = LV_MAX(points[0].x, points[1].x);
            fill_area.y1 = LV_MIN(points[0].y, points[1].y);
            fill_area.y2 = series_area.y2;
            
            lv_draw_rect(dsc->draw_ctx, &rect_dsc, &fill_area);
        }
    }
}

