// --- Fonction d'ajout avec lissage ---
void baro_add_value(float pression) {
    // Convertit en "raw" = dixième de hPa entier (ex: 1019.8 -> 10198)
    int raw = (int)roundf(pression * 10.0f);

    // Décaler le tableau vers la gauche (perd la plus ancienne valeur)
    memmove(&baro_points[0],
            &baro_points[1],
            (NB_POINTS_BARO - 1) * sizeof(baro_points[0]));

    // Place la nouvelle valeur à la fin (chronologique)
    baro_points[NB_POINTS_BARO - 1] = (lv_coord_t) raw;

    // (Optionnel) mettre à jour un index logique si tu en as besoin ailleurs
    //baro_index = (baro_index + 1) % NB_POINTS_BARO; // pas vraiment utile ici

    ESP_LOGI(TAG_BARO, "⏱️ Ajout pression: %.2f hPa -> raw=%d", pression, raw);

    // Mise à jour de l'échelle et rafraîchissement du graphe
    update_baro_chart_scale(ui_graphe);
    lv_chart_refresh(ui_graphe);
}

// --- Mise à jour de l'échelle Y ---
void update_baro_chart_scale(lv_obj_t *chart)
{
    int min_raw = 99999;
    int max_raw = 0;
    for (int i = 0; i < NB_POINTS_BARO; i++) {
        int v = baro_points[i];
        if (v > 0) {
            if (v < min_raw) min_raw = v;
            if (v > max_raw) max_raw = v;
        }
    }
    if (min_raw == 99999 || max_raw == 0) return;
    int min_y = min_raw - 10;  // marge -1.0 hPa
    int max_y = max_raw + 10;  // marge +1.0 hPa
    lv_chart_set_range(chart, LV_CHART_AXIS_PRIMARY_Y, min_y, max_y);
}